import React from 'react';
import '../App.css';

class NoteListComponent extends React.Component {
  constructor(props) {
    super(props)
    this.state = {

      note: {
        id: -1,
        title: 'New Note'
      },

      notes: [
        {id: 123, title: "Folder 1"},
        {id: 124, title: "Folder 2"},
        {id: 125, title: "Folder 3"}
      ]
    }

  }

  createNote = () => {
    this.state.note.id = (new Date()).getTime();
    this.setState({
                    notes: [this.state.note, ...this.state.notes]
                  })
  }

  titleChanged = (event) => {
    this.setState({
                    note: {
                      title: event.target.value
                    }
                  })
  }

  deleteNote = (id) => {
    this.setState({
                    notess: this.state.notes.filter(note => note.id !== id)
                  })
  }

  render() {
    return (
        <ul className="list-group">
          <li className="list-group-item">
            <input
                onChange={this.titleChanged}
                className="form-control"/>
            <button onClick={this.createFolder} className="btn btn-primary btn-block">
              Add Folder
