import React from 'react';
import FolderComponent from './FolderComponent';
import '../App.css';

class FolderListComponent extends React.Component {
    constructor(props) {
        super(props)
        this.state = {

            folder: {
                id: '',
                title: ''
            },

            folders: [
                {id: "123", title: "folder 1"},
                {id: "124", title: "folder 2"},
                {id: "125", title: "folder 3"}
            ]
        }

    }

    componentDidMount() {
        this.setState({
                          folder: 'A Folder'
                      });
    }

    // Handles the input field input
    handleOnChange = event => {
        this.setState({folder: event.target.value});
    };

    render() {
        return (
                <ul className="list-group">
                    <li className="list-group-item">
                        <input
                            value={this.state.folder.title}
                            className="form-control"/>
                        <button className="btn btn-primary btn-block">Add Folder</button>
                    </li>
                    { this.state.folders.map(
                        folder =>
                            <FolderComponent
                            folder={folder}/>
                        )
                    }
                </ul>
        )

    }
}

export default FolderListComponent;
