import React from "react";
import {Link} from "react-router-dom";
import JokeService from "../services/jokeService";
import {findJoke} from "../actions/jokeActions";
import {connect} from "react-redux";

const jokeService = new JokeService();

class SearchDetailsComponent extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            joke: ''
        }
    }

    componentDidMount() {
        this.props.findJokeById(this.props.resultId)
    }

    findJoke = (jokeId) => {
        fetch(`https://icanhazdadjoke.com/j/${jokeId}`, {
            headers: {
                Accept: "application/json"
            }
        })
            .then(response => response.json()).then(this.setJoke)
    };

    setJoke = (search) => {
        this.setState({
            joke: search.joke
        })
    };

    render() {
        return (
            <div className={"container-fluid"}>
                <h1>{this.props.joke}</h1>
                <br/>
                <h5>Users who have saved this joke:</h5>
                <ul>
                    {/*
                    Below should be fetched from local API
                    Find users that currently have the particular resultId saved as public note
                    */}
                    <li>
                        <Link to={"/profiles/user1"}>
                            User 1
                        </Link>
                    </li>
                    <li>
                        <Link to={"/profiles/user2"}>
                            User 2
                        </Link>
                    </li>
                    <li>
                        <Link to={"/profiles/user3"}>
                            User 3
                        </Link>
                    </li>
                    <li>
                        <Link to={"/profiles/user4"}>
                            User 4
                        </Link>
                    </li>
                    <li>
                        <Link to={"/profiles/user5"}>
                            User 5
                        </Link>
                    </li>
                </ul>
            </div>
        );
    }
}

const stateToPropertyMapper = (state) => ({
    joke: state.jokes.joke
});
const dispatchToPropertyMapper = (dispatch) => ({
    findJokeById: (jokeID) => {
        jokeService.findJokeById(jokeID)
            .then(joke => {dispatch(findJoke(joke))})
    }
});
export default connect(
    stateToPropertyMapper,
    dispatchToPropertyMapper
)(SearchDetailsComponent)
