import React from 'react';
import {Dropdown} from "react-bootstrap";
import LabelComponent from './LabelComponent';
import '../App.css';

class NoteComponent extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
        }

    }

    // Handles the input field input
    handleOnChange = event => {
        this.setState({note: event.target.value});
    };

    render() {
        return (
            <div>
                <li className="list-group-item">
                    <div className="form-group">
                        <h5 className="select">Visibility</h5>
                        <select className="form" id="exampleFormControlSelect1">
                            <option>Public</option>
                            <option>Private</option>
                        </select>
                    </div>
                    <div className="card">
                        <input
                            type="text"
                            className="form-control new-course-title"
                            placeholder="Take a Note!"
                            onChange={this.handleOnChange}
                        />
                        <button
                            className="btn btn-success btn-sm mx-1"
                            onClick={this.saveCourseTitle}
                        >
                            Save
                        </button>
                        <div className="card-body">
                            <p className="card-text">{this.state.note}</p>
                            {/* LabeListComponent instead of Label Conmponent */}
                            <LabelComponent/>
                        </div>
                        {/* TODO ADD LABEL */}
                    </div>

                </li>
            </div>
        )

    }

}

export default NoteComponent;
