import React from 'react';
import '../../App.css';
import {connect} from "react-redux";
import userService from "../../services/userService";
import {findCurrentUser} from "../../actions/userActions";
import labelService from "../../services/labelService";
import LabelComponent from "./LabelComponent";
import {createLabel, deleteLabel, findLabelsForUser} from "../../actions/labelActions";

const UserService = new userService();
const LabelService = new labelService();

class LabelListComponent2 extends React.Component {
    state = {
        newTitle: ""
    };

    componentDidMount() {
        this.props.findCurrentUser();
        this.props.findLabelsForUser(this.props.user.id);
    }

    titleChanged = (e) => {
        this.setState({
            newTitle: e.target.value
        })
    };

    createLabel = () => {
        this.props.createLabel(this.props.user.id, {title: this.state.newTitle})
    };

    deleteLabel = (id) => {
        this.props.deleteLabel(id)
    };

    render() {
        return (
            <div className={"container"}>
                <div className={"row"}>
                    <input
                        onChange={this.titleChanged}
                        className="form-control"
                        placeholder={"New Label"}
                    />
                </div>
                <div className={"row"}>
                    <button onClick={this.createLabel} className="btn btn-primary btn-block">
                        Add Label
                    </button>
                </div>
                <br/>
                <div className={"row"}>
                    {this.props.labels && this.props.labels.map(label =>
                        <div key={label.id}>
                            <LabelComponent
                                label={label}
                                deleteLabel={this.deleteLabel}
                            />
                        </div>
                    )}
                </div>
            </div>
        )
    }
}

const stateToPropertyMapper = (state) => ({
    user: state.user.user,
    labels: state.labels.labels
});
const dispatchToPropertyMapper = (dispatch) => ({
    findCurrentUser: () => {
        UserService.findCurrentUser().then(user => {
            dispatch(findCurrentUser(user))
        })
    },
    findLabelsForUser: (userId) => {
        LabelService.findLabelsForUser(userId).then(labels => {
            dispatch(findLabelsForUser(labels))
        })
    },
    createLabel: (userId, label) => {
        LabelService.createLabel(userId,label).then(label => {
            dispatch(createLabel(label))
        })
    },
    deleteLabel: (labelId) => {
        LabelService.deleteLabel(labelId).then(status => {
            dispatch(deleteLabel(labelId))
        })
    }
});
export default connect(
    stateToPropertyMapper,
    dispatchToPropertyMapper
)(LabelListComponent2)
