import React from 'react';
import FolderComponent from './FolderComponent';
import '../App.css';

class NoteListComponent extends React.Component {
  constructor(props) {
    super(props)
    this.state = {

      Note: {
        id: -1,
        title: 'New Note'
      },

      notes: [
        {id: 123, title: "Note 1", text: 'wow'},
        {id: 124, title: "Note 2", text: 'wow cool'},
        {id: 125, title: "Note 3", text: 'wow this'}
      ]
    }

  }

  createNote= () => {
    this.state.Note.id = (new Date()).getTime();
    this.setState({
                    notes: [this.state.note, ...this.state.notes]
                  })
  }

  titleChanged = (event) => {
    this.setState({
                    note: {
                      title: event.target.value
                    }
                  })
  }

  deleteNote = (id) => {
    this.setState({
                    Notes: this.state.notes.filter(note => note.id !== id)
                  })
  }

  render() {
    return (
        <ul className="list-group">
          <li className="list-group-item">
            <input
                onChange={this.titleChanged}
                className="form-control"/>
            <button onClick={this.createNote} className="btn btn-primary btn-block">
              Add Note
            </button>
          </li>
          {this.state.notes.map(
              note =>
                  <NoteComponent
                      deleteNote={this.deleteNote}
                      note={note}
                      key={note.id}/>
          )
          }
        </ul>
    )

  }
}

export default NoteListComponent;
