import React from 'react';
import '../../App.css';

class NewNoteComponent extends React.Component {
    state = {
        newTitle: "New Note",
        newText: "Note Body",
        newStatus: "private"
    };

    //TODO:
    //Add in label addition

    titleChanged = (e) => {
        this.setState({
            newTitle: e.target.value
        })
    };

    textChanged = (e) => {
        this.setState({
            newText: e.target.value
        })
    };

    statusChanged = (e) => {
        this.setState({
            newStatus: e.target.value
        })
    };

    //TODO:
    //Add functionality to determine if user or folder should be created
    createNote = () => {
        const note = {
            title: this.state.newTitle,
            note: this.state.newText,
            status: this.state.newStatus
        };
        this.props.createUserNote(note);
        // this.props.createFolderNote(note)
    };

    render() {
        return (
            <div>
                <div className="form-group row">
                    <label htmlFor="addTitle"
                           className="col-sm-2 col-form-label">
                        Title
                    </label>
                    <div className="col-sm-10">
                        <input type="text"
                               className="form-control "
                               id="addTitle"
                               placeholder="New Title (if applicable)"
                               onChange={this.titleChanged}
                        />
                    </div>
                </div>
                <div className="form-group row">
                    <label htmlFor="addText"
                           className="col-sm-2 col-form-label">
                        Body
                    </label>
                    <div className="col-sm-10">
                        <input type="text"
                               className="form-control "
                               id="addText"
                               placeholder={"Note Body"}
                               onChange={this.textChanged}
                        />
                    </div>
                </div>
                <div className="form-group row">
                    <label htmlFor="addStatus"
                           className="col-sm-2 col-form-label">
                        Status
                    </label>
                    <div className="col-sm-10">
                        <select className="form-control"
                                id="editRole"
                                placeholder={"private"}
                                onChange={this.statusChanged}
                        >
                            <option value={"private"}>Private</option>
                            <option value={"public"}>Public</option>
                        </select>
                    </div>
                </div>
                <button className="btn btn-primary"
                        onClick={this.createNote}>
                    Add Note
                </button>
            </div>
        )
    }
}

export default NewNoteComponent;
