import React from 'react';
import LabelComponent from './LabelComponent';
import '../App.css';

class FolderComponent extends React.Component {
    constructor(props) {
        super(props)
        this.state = {

            folder: 'Folder'
        }


    }
    componentDidMount() {
        this.setState({
                          folder: 'A Folder'
                      });
    }
    // Handles the input field input
    handleOnChange = event => {
        this.setState({ folder: event.target.value });
    };


    render() {
        return <div>
            {/* TODO: DROPDOWNS FOR EVERYTHING PUBLIC OR PRIVATE - */}
            <li className="list-group-item">

                <div class="card" style={{width: '18rem'}}>
                    <input
                        type="text"
                        className="form-control new-course-title"
                        placeholder="Folder Name"
                        onChange={this.handleOnChange}
                    /> <button
                    className="btn btn-success btn-sm mx-1"
                    onClick={this.saveCourseTitle}
                >
                    Save
                </button>
                    <div class="card-body">
                        <h5 class="card-title">{this.state.folder}</h5>
                    </div>
                    {/* TODO ADD LABEL */}
                </div>

            </li>
        </div>

    }


}

export default FolderComponent;
