import React from 'react';
import '../App.css';
import LabelListComponent from "./label/LabelListComponent";
import NoteListComponent from "./note/NoteListComponent";
import FolderListComponent from "./folder/FolderListComponent"
import {findCurrentUser} from "../actions/userActions";
import {connect} from "react-redux";
import userService from "../services/userService";

const UserService = new userService();

class DefaultLayoutComponent extends React.Component {
    componentDidMount() {
        console.log(this.props.findCurrentUser());
    }
    render() {
        return (
            <div className="App">
                <br/>
                <div className={"row"}>
                    <div className="col-1">

                    </div>
                    <div className="col-2">
                        <LabelListComponent/>
                    </div>
                    <div className="col-6">
                        <NoteListComponent/>
                    </div>
                </div>
            </div>
        )
    }
}

const stateToPropertyMapper = (state) => ({
    user: state.user.user,
    notes: state.notes.notes
});
const dispatchToPropertyMapper = (dispatch) => ({
    findCurrentUser: () => {
        UserService.findCurrentUser().then(user => {
            dispatch(findCurrentUser(user))
        })
    },
    findFoldersForUser: (userId) => {
        FolderService.findFoldersForUser(userId).then(folders => {
            dispatch(findFoldersForUser(folders))
        })
    },
    createFolder: (userId, folder) => {
        FolderService.createFolder(userId,folder).then(folder => {
            dispatch(createFolder(folder))
        })
    },
    deleteFolder: (folderId) => {
        FolderService.deleteFolder(folderId).then(status => {
            dispatch(deleteFolder(folderId))
        })
    }
});
export default connect(
    stateToPropertyMapper,
    dispatchToPropertyMapper
)(DefaultLayoutComponent)
