import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import {Button, Form, FormControl, Nav, Navbar, Dropdown, ButtonGroup} from "react-bootstrap";
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
} from "react-router-dom";
import Home from "./containers/Home";
import SearchComponent from "./components/SearchComponent";
import SearchDetailsComponent from "./components/SearchDetailsComponent";
import EditProfileComponent from "./components/profile/EditProfileComponent";
import PublicProfileComponent from "./components/profile/PublicProfileComponent"
import LoginComponent from "./components/LoginComponent";
import RegisterComponent from "./components/RegisterComponent";
import { combineReducers, createStore } from "redux";
import { Provider } from "react-redux";
import jokeReducer from "./reducers/jokeReducer";
import profileReducer from "./reducers/profileReducer";
import userReducer from "./reducers/userReducer";
import folderReducer from "./reducers/folderReducer";
import noteReducer from "./reducers/noteReducer";
import labelReducer from "./reducers/labelReducer";


class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            keyword: ''
        }
    }

    reducers = combineReducers({
        jokes: jokeReducer,
        profile: profileReducer,
        user: userReducer,
        folders: folderReducer,
        notes: noteReducer,
        labels: labelReducer
    });

    store = createStore(this.reducers);

    keyWordChange = event =>
        this.setState({ keyword: event.target.value });

    render() {
        return (
            <Provider store={this.store}>
                <Router>
                    <div>
                        <Navbar bg="dark" variant="dark" expand="lg">
                            <Navbar.Brand href="/">Mind-Assistant</Navbar.Brand>
                            <Navbar.Toggle aria-controls="basic-navbar-nav" />
                            <Navbar.Collapse id="basic-navbar-nav">
                                <Nav className="mr-auto">
                                    <Form inline>
                                        <FormControl type="text" placeholder="Search" className="mr-sm-2" />
                                        <Button variant="outline-success">Search</Button>
                                    </Form>
                                </Nav>
                                <Form inline>
                                    <FormControl type="text" placeholder="Search" className="mr-sm-2" />
                                    <Button variant="outline-success">Search</Button>
                                </Form>
                                <Dropdown as={ButtonGroup}>
                                    <Button variant="dark">Log In</Button>

                                    <Dropdown.Toggle split variant="success" id="dropdown-split-basic" />

                                    <Dropdown.Menu>
                                        <Dropdown.Item href="#/action-1">Action</Dropdown.Item>
                                        <Dropdown.Item href="#/action-2">Another action</Dropdown.Item>
                                        <Dropdown.Item href="#/action-3">Something else</Dropdown.Item>
                                    </Dropdown.Menu>
                                </Dropdown>
                            </Navbar.Collapse>
                        </Navbar>

                        <nav className="navbar navbar-expand navbar-dark bg-dark">
                            <a className="navbar-brand" href="/">Mind-Assistant</a>

                            <div className="collapse navbar-collapse">
                                <ul className="navbar-nav mr-auto">
                                </ul>
                                {/*<NavDropdown title="Dropdown" id="basic-nav-dropdown">*/}
                                {/*    <NavDropdown.Item href="#action/3.1">Action</NavDropdown.Item>*/}
                                {/*    <NavDropdown.Item href="#action/3.2">Another action</NavDropdown.Item>*/}
                                {/*    <NavDropdown.Item href="#action/3.3">Something</NavDropdown.Item>*/}
                                {/*    <NavDropdown.Divider />*/}
                                {/*    <NavDropdown.Item href="#action/3.4">Separated link</NavDropdown.Item>*/}
                                {/*</NavDropdown>*/}
                                <form className="form-inline my-2 my-lg-0">
                                    <input value={this.state.keyword}
                                           onChange={this.keyWordChange}
                                           className="form-control mr-sm-2"
                                           type="search"
                                           placeholder="Search  for Dad Jokes"
                                           aria-label="Search" />
                                    <Link to={`/search/${this.state.keyword}`}>
                                        <button className="btn btn-outline-success my-2 my-sm-0"
                                                type="submit">
                                            Search
                                        </button>
                                    </Link>
                                </form>
                                <a className="nav-link btn-dark" href="/login">Log In</a>
                            </div>
                        </nav>

                        <Switch>


                            <Route exact path={"/"}
                                render={(props) =>
                                    <Home {...props}
                                    />
                                }
                            />


                            <Route
                                exact path={["/search",
                                            "/search/:criteria"]}
                                render={(props) =>
                                    <SearchComponent
                                        {...props}
                                        criteria={props.match.params.criteria}
                                    />
                                } />
                            <Route
                                exact path={"/search/:criteria/details/:resultId"}
                                render={(props) =>
                                    <SearchDetailsComponent
                                        {...props}
                                        criteria={props.match.params.criteria}
                                        resultId={props.match.params.resultId}
                                    />
                                }/>
                            <Route
                                exact path={"/profile/:profileId"}
                                render={(props) =>
                                    <PublicProfileComponent
                                        {...props}
                                        profileId={props.match.params.profileId}
                                    />
                                }/>
                            <Route
                                exact path={"/profile"}
                                render={(props) =>
                                    <EditProfileComponent
                                        {...props}
                                    />
                                }/>
                            <Route
                                exact path={"/login"}
                                render={(props) =>
                                    <LoginComponent
                                        {...props}
                                    />
                                }/>
                            <Route
                                exact path={"/register"}
                                render={(props) =>
                                    <RegisterComponent
                                        {...props}
                                    />
                                }/>
                            <Route path={"/"}>
                                <Home />
                            </Route>
                        </Switch>
                    </div>
                </Router>
            </Provider>
        );
    }
}

export default App;
